const[edit,setEdit]=useState("edit")



const inputEvent = (event)=>{
  setFName(event.target.value)
}

const addname =()=>{
setFullName(fName)
setFName("")
}
const editbtn=()=>{
  setFullName("")
  setEdit("save")
}

const editEvent=(event)=>{
  setFullName(event.target.value)
}



  return (
    <>
    <input type="text" placeholder="enter first name" onChange={inputEvent} value={fName} />
    <button onClick={addname}>Click to Add</button>
    <h1>hello world this is {fullName} <input type="text" placeholder="enter edited name" onChange={editEvent} value={fullName}/> <button onClick={editbtn}> {edit}</button></h1>
    </>
  );
}

