import React,{useEffect} from 'react'




const Scroll = () => {

const [state, setState] = React.useState([])
const [page, setPage] = React.useState(null)

useEffect(() => {
  
    fetch(`https://api.instantwebtools.net/v1/passenger?page=${page}&size=5`)
    .then(response => response.json())
    .then(json => setState([...state,...json.data]))
console.log("response",json);
}, [page])

const scrollToEnd = ()=>{
    setPage(page + 1)
}


window.onscroll = function(){
    //check if the page has scolled to the bottom
if (
    window.innerHeight+ document.documentElement.scrollTop === document.documentElement.offsetHeight
) {
    scrollToEnd()
}
    
};

  return (

    <div>
    {state.length > 0 && state.map((elem , i)=>{
<div key={i} className="main_div">

    <h4>ID : </h4>
    <h4></h4>
    <h4></h4>
    <h4></h4>
</div>
    })}

    </div>
  )
}

export default Scroll;
